import React, { useState } from "react";

function App() {
  const [file, setFile] = useState(null);
  const [response, setResponse] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return alert("Please select a file");

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      const data = await res.json();
      setResponse(data);
    } catch (err) {
      console.error("Error uploading file:", err);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold mb-4">File Cleansing App</h1>
      <input type="file" onChange={handleFileChange} className="mb-4" />
      <button
        onClick={handleUpload}
        className="px-4 py-2 bg-blue-500 text-white rounded"
      >
        Upload
      </button>

      {response && (
        <div className="mt-6">
          <h2 className="font-semibold">Analysis</h2>
          <p><b>Word Count:</b> {response.analysis.word_count}</p>
          <p><b>Unique Words:</b> {response.analysis.unique_words}</p>
          <p className="mt-2"><b>Preview:</b> {response.analysis.preview}</p>
        </div>
      )}
    </div>
  );
}

export default App;
